import React, { useState, useEffect } from 'react';
import CarForm from './components/CarForm';
import axios from 'axios';
import './App.css';
import SecondTable from './components/SecondTable';
import ThirdTable from './components/ThirdTable';


axios.defaults.baseURL = "http://localhost:8081";

function App() {
  const [cars, setCars] = useState([]);
  const [pendingCars, setPendingCars] = useState([]);

  useEffect(() => {
    // Fetch all cars from the backend
    axios.get('/api/cars')
      .then(response => {
        setCars(response.data);
      })
      .catch(error => console.error('There was an error!', error));
  }, []);

  const handleAddCar = (car) => {
    setPendingCars([...pendingCars, car]);
  };
  const handleDelete = (index) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this car?");
    if (confirmDelete) {
      const updatedCars = pendingCars.filter((_, i) => i !== index);
      setPendingCars(updatedCars);
    }
  };

  const handleDeleteCars = (index) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this car?");
    if (confirmDelete) {
      const updatedCars = cars.filter((_, i) => i !== index);
      setCars(updatedCars);
    }
  };

  const handleUpdateCar = (index) => {
    const carToUpdate = pendingCars[index];

    setCars([...cars, carToUpdate]);

    // Remove the car from `pendingCars`
    const updatedPendingCars = pendingCars.filter((_, i) => i !== index);
    setPendingCars(updatedPendingCars);
    // }
  };

  const handleUpdateCars = (index, field, value) => {
    const updatedCars = cars.map((car, i) =>
      i === index ? { ...car, [field]: value } : car
    );
    setCars(updatedCars);
  };

  const handleRegister = () => {
    axios.post("/api/cars", cars)
      .then((response) => {  // Fixed typo here
        console.log(response.data);
        // Optionally, add any logic to handle successful registration
      })
      .catch((error) => {
        console.error("Error during registration:", error);
        alert("Error: " + error.message || error); // More user-friendly error message
      });
  };


  return (
    <div className="App">
      <h1>Car Registration</h1>
      <CarForm onAddCar={handleAddCar} />
      {pendingCars && pendingCars.length > 0 && (
        <SecondTable pendingCars={pendingCars} onDelete={handleDelete} onUpdate={handleUpdateCar} />)}
      {cars && cars.length > 0 && (
        <>
          <ThirdTable cars={cars} onDelete={handleDeleteCars} onUpdate={handleUpdateCars} />
          <button type='button' onClick={handleRegister}>Register</button>
        </>
      )}

    </div>
  );
}

export default App;
